INSERT INTO man_recipe (id, "name", description, measure_quantity, created_at, updated_at, recipe_category_id, measure_unit) VALUES(1,'Massa Branca - Óleo','1.Na batedeira, adicionar os ovos, óleo, açúcar e baunilha\n2.Bater por aproximadamente 5min com o globo\n.Depois de tudo incorporado, acrescentar os secos e o leite em 3 partes e bater na velocidade mínima até incorporar\n.Porcionar nas formas\n.Assar a 130 graus',4900.0,'2000-01-01 00:00:00','2025-06-22 02:31:57.082290',1,'g');
INSERT INTO man_recipe (id, "name", description, measure_quantity, created_at, updated_at, recipe_category_id, measure_unit) VALUES(2,'Massa Chocolate - Óleo','1.Na batedeira, adicionar os ovos, óleo, açúcar e baunilha\r\n 2.Bater por aproximadamente 5min com o globo\r\n 3.Depois de tudo incorporado, acrescentar os secos e o leite em 3\r\n partes e bater na velocidade mínima até incorporar\r\n 4.Porcionar nas formas\r\n 5.Assar a 130°',4900.0,'2000-01-01 00:00:00','2025-06-22 02:31:43.101024',1,'g');
INSERT INTO man_recipe (id, "name", description, measure_quantity, created_at, updated_at, recipe_category_id, measure_unit) VALUES(3,'Massa Cenoura - Óleo','1.Descascar e cortar em rodelas a cenoura\r\n2.No liquidificador bater o açucar, ovos, óleo e cenoura\r\n3.Bater até ficar homogêneo\r\n4.Em um bowl grande, separar a farinha, fermento e sal\r\n5.Transferir o liquido para o bowl e misturar até ficar homogêneo\r\n6.Porcionar nas formas, de acordo com os tamanhos\r\n7.Assar em forno pré-aquecido a 130º',2700.0,'2000-01-01 00:00:00','2025-06-22 02:31:52.085271',1,'g');
